                                if (typeof ga !== 'undefined') { ga('send', 'event', 'Adblock', 'No', {'nonInteraction': 1}); }
                                else if (typeof _gaq !== 'undefined') { _gaq.push(['_trackEvent', 'Adblock', 'No', undefined, undefined, true]); }
                              }
                            }      
                          }, 1);
      });
    });
